class Solution:
    def is_valid(self, code: str) -> bool:
        start_tag, tag_content, end_tag = self.parse_code(code)
        if start_tag and end_tag and self.validate_tag(start_tag, end_tag, tag_content):
            return True
        return False

    def parse_code(self, code: str) -> tuple:
        tag_content, index = "", 0
        while index < len(code):
            if code[index] == '<':
                start_tag = self.get_tag(code, index)
                return start_tag, tag_content, tag_content + start_tag[-1]
            tag_content += code[index]
            index += 1
        return None, tag_content, None

    def get_tag(self, code: str, index: int) -> str:
        tag_name, is_cdata, content_index = "", False, index
        while index < len(code):
            if code[index] == '!':
                if code[index + 1] == '[' and not is_cdata:
                    is_cdata = True
                elif code[index] == '/' and code[index + 1] == '[' and content_index > index + 2:
                    self.read_until_cdata(code, content_index)
                    return tag_name[-1:] + ">"
                elif code[index:index + 4] == "</" and len(tag_name) > 0:
                    return tag_name
                else:
                    index += 1
                    continue

            if not is_cdata:
                if code[index] == '>':
                    break
                if code[index] == '/':
                    is_cdata = True
                tag_name += code[index]

            index += 1
        self.read_until_cdata(code, index)
        return tag_name

    def read_until_cdata(self, code: str, index: int) -> None:
        while index < len(code):
            if code[index] == ']':
                return
            index += 1

    def validate_tag(self, start_tag: str, end_tag: str, tag_content: str) -> bool:
        if len(start_tag) < 1 or len(start_tag) > 9:
            return False
        if start_tag != end_tag:
            return False
        if self.is_unmatched(start_tag, tag_content):
            return False
        open_tags = {start_tag}
        for index, candidate_tag in enumerate(tag_content):
            if candidate_tag == '<':
                nested_start_tag = self.get_tag(tag_content, index)
                if nested_start_tag is not None:
                    open_tags.add(nested_start_tag)
            if candidate_tag == '</':
                nested_end_tag = self.get_tag(tag_content[index + 1:], index)
                if nested_end_tag is not None:
                    if nested_end_tag in open_tags:
                        open_tags.remove(nested_end_tag)
                    else:
                        return False
        return not open_tags

    def is_unmatched(self, tag: str, content: str) -> bool:
        return tag not in content and content not in content.split(tag)
```

