    def longestDecomposition(self, text: str) -> int:
        n = len(text)
        prev = [None] * (n + 1)
        curr = [None] * (n + 1)
        palindrome_len = [0] * (n + 1)

        max_len = 0
        for i in range(n-1, -1, -1):
            for j in range(i+1, n+1):
                if j - i <= 2:
                    if text[i] == text[j]:
                        palindrome_len[j] = 2 if (j - i) == 0 else palindrome_len[i+1] + 2
                        if palindrome_len[j] > max_len:
                            max_len = palindrome_len[j]
                else:
                    if text[i] == text[j] and palindrome_len[mid] != None for mid in range(i+1, j)):
                        len_mid = palindrome_len[mid]
                        palindrome_len[j] = len_mid + 2
                        if palindrome_len[j] > max_len:
                            max_len = palindrome_len[j]

        if max_len == 0:
            return 1

        # Find the index of the middlemost palindrome
        start = 0
        end = min(max_len, n)
        while end - start > max_len:
            start += palindrome_len[start]
            end -= palindrome_len[end]

        # Store the half-length palindromes for both sides of the middle-most palindrome
        left_half = []
        for i in range(start - max_len // 2, start):
            left_half.append(text[i])

        right_half = text[end:][::-1]
        result = max_len // 2 + len(left_half) + len(right_half)

        # If the remaining text is also a palindrome, we can include it to increase the length
        for pal_len in palindrome_len[start:end]:
            if pal_len > max_len // 2 + len(left_half) + len(right_half):
                temp = max_len // 2 + len(left_half) + len(right_half) + pal_len - max_len
                if temp > result:
                    result = temp

        return result
