def decompose(num):
    dp = [[-1] * (num + 1) for _ in range(21)]

    for i in range(1, num + 1):
        for k in range(1, 21):
            for j in range(1, i + 1):
                if j < pow(k, k):
                    continue
                power = pow(k, k - 1)
                sum_so_far = power
                while sum_so_far + power <= i:
                    sum_so_far += power
                    power *= k
                dp[i][k] = max(dp[i][k], [k] + dp[i - sum_so_far][k - 1])

