        num -= sum(pow(arr[idx], i) for i, arr in enumerate(dp[num][:idx]))
        result.append(idx)
        remainder[k] = num
        num = remainder[k]

    return result[::-1] + [remainder[-1]] if num == 0 else result + [remainder[-1], num]
