def power(a, b, mod):
  if b == 0:
    return 1
  else:
    return a * power(a * a % mod, b - 1, mod) % mod

def countNumber(primes, exponents, min_exponents, index, prod, n, memo):
  key = (tuple(sorted(primes)), *tuple(sorted(exponents)), *tuple(sorted(min_exponents)), index, prod)
  if key in memo:
    return memo[key]

  if index == len(primes):
    memo[key] = 1 if prod <= n else 0
    return memo[key]

  count = 0
  for exp in range(max(min_exponents[index], exponents[index]), min(exponents[index] + 1, 10 ** 5)):
    p = prod * power(primes[index], exp, 10 ** 9 + 7)
    if p <= n:
      for j in range(index + 1, len(primes)):
        if exponents[j] >= min_exponents[j]:
          count += countNumber(primes, exponents, min_exponents, j, p, n, memo)
      memo[key] = count
    count += memo[key]

