import math

def gcd(a, b):
    if b == 0:
        return a
    return gcd(b, a % b)

def MCM(n):
    if n == 1:
        return 1
    primes = [2]
    i = 3
    while len(primes) < n:
        is_prime = True
        for p in primes:
            if i % p == 0:
                is_prime = False
                break
        if is_prime:
            primes.append(i)
        i += 2
    phi = n
    for p in primes:
        if p*p > n:
            break
        count = 0
        for j in range(n + 1):
            if j % p != 0:
                count += 1
        phi -= phi // p * count
    return phi

def indulge(n):
    for a in range(1, 4 * n + 1):
        for b in range(2, 4 * n + 1):
            if a != b and gcd(a, b) <= 1 and math.gcd(a, b) != 1 and a % b == 0 or b % a == 0:
                return False
    return True

def solutions(n):
    if indulge(n):
        return [-1]
    chairs = list(range(1, 4 * n + 1))
    primes = [2]
    i = 3
    result = []
    while len(primes) < n:
        is_prime = True
        for p in primes:
            if i % p == 0:
                is_prime = False
                break
        if is_prime:
            result.append(primes[-1])
            primes.append(i)
        i += 2
    while len(result) < n:
        p = primes.pop(-1)
        num_smaller_primes = sum(1 for pr in primes if pr < int(math.sqrt(p))+1)
        multiple = math.ceil(p * (4 * n - len(result)) / (4 * n - num_smaller_primes))
