import math

class Edge:
    def __init__(self, to, cost, id_):
        self.to = to
        self.cost = cost
        self.id = id_

class Graph:
    def __init__(self):
        self.adj = {}
        self.reverse_adj = {}
        self.node_ids = {}

    def add_edge(self, u, v, cost):
        if u not in self.adj:
            self.node_ids[u] = len(self.adj)
        if v not in self.adj:
            self.node_ids[v] = len(self.adj)
        self.adj[u].append(Edge(v, cost, self.node_ids[u] + len(self.adj[u])))
        self.reverse_adj[v].append(Edge(u, cost, self.node_ids[v] + len(self.reverse_adj[v])))

    def dijkstra(self, start):
