        costs = {}
        shortest_path[start] = [0]
        costs[start] = 0
        pq = [(0, start)]
        while pq:
            (cost, u) = heapq.heappop(pq)
            if u in shortest_path:
                continue
            shortest_path[u] = []
            costs[u] = cost
            for edge in self.adj[u]:
