```python
import heapq
from heapq import heappush, heappop
from math import inf

def dijkstra(graph, start_node):
    distances = {node: inf for node in graph}
    distances[start_node] = 0
    priority_queue = [(0, start_node)]

    while priority_queue:
        current_distance, current_node = heappop(priority_queue)
        if current_distance > distances[current_node]:
            continue

        for neighbor, weight in graph[current_node]:
            distance = current_distance + weight
            if distance < distances[neighbor]:
                distances[neighbor] = distance
                heappush(priority_queue, (distance, neighbor))

    return distances

def find_shortest_special_pair_distance(N, M, K, special_nodes, graph):
    for node in special_nodes:
        graph[node] = {neighbor: weight for neighbor, weight in graph if node != neighbor}

    shortest_distances = {}
    for special_node in special_nodes:
        distances = dijkstra(graph, special_node)
        shortest_distances[special_node] = distances

    min_distance = inf
    for node1 in special_nodes:
        for node2 in special_nodes:
            if node1 == node2:
                continue
            min_distance = min(min_distance,
                               shortest_distances[node1][node2],
                               shortest_distances[node2][node1])

    return min_distance

def parse_input(input_lines):
    lines = list(input_lines)
    N, M, K = map(int, lines.pop(0).split())
    special_nodes = list(map(int, lines.pop(0).split()))
    graph = {}
