        opening_braces_count = opening_braces
        while opening_braces_count > 0:
            operator = stack.pop()
            if operator == '(':
                opening_braces_count -= 1
            else:  # operator must be ')'
                closing_braces_count += 1
                while closing_braces_count > opening_braces_count:
                    operator = stack.pop()
                    if operator == '+' or operator == '-':
                        apply_operator(stack, operator, operand)
                    else:
                        stack.append(operator)

    def process_operators(stack):
        while stack:
            operator = stack.pop()
            if operator == '+' or operator == '-':
                apply_operator(stack, operator, operand)
            else:
                stack.append(operator)

    operands = []
    opening_braces = s.count('(')
    if opening_braces != s.count(')' :
        raise ValueError("Uneven number of braces.")

