#include <algorithm>
#include <vector>
#include <numeric>

const int MOD = 998244353;

long long power(long long a, long long n) {
    long long res = 1;
    while (n) {
        if (n & 1) res = (res * a) % MOD;
        a = (a * a) % MOD;
        n >>= 1;
    }
    return res;
}

long long binomial(long long n, long long k) {
    long long res = 1;
    res = (res * power(n, k)) % MOD;
    res = (res * power(k, MOD - 2)) % MOD;
    return res;
}

long long countPartitions(const std::vector<int>& nums) {
    int size = nums.size();
    std::vector<std::vector<long long>> C(size + 1, std::vector<long long>(size + 1));

    for (int i = 1; i <= size; ++i)
        for (int j = std::min(i, size); j >= 1; --j)
            C[i][j] = (C[i - 1][j] + C[i][j + 1]) % MOD;

    std::vector<std::vector<long long>> dp(size + 1, std::vector<long long>(size + 1));

    for (int i = 1; i <= size; ++i) {
        for (int j = std::min(i, size); j >= 1; --j) {
            int ni = i, nj = j;
            std::vector<int> niSorted = nums.substr(0, ni);
            std::sort(niSorted.begin(), niSorted.end());
            std::reverse(nums.substr(0, nj).begin(), nums.substr(0, nj).end());
            long long niSum = std::accumulate(niSorted.begin(), niSorted.end(), 0);
            long long njSum = std::accumulate(nums.substr(0, nj).rbegin(), nums.substr(0, nj).rend(), 0);
