import sys

def find(x, parents):
    if parents[x] != x:
        parents[x] = find(parents[x], parents)
    return parents[x]

def union(x, y, parents, ranks):
    root_x = find(x, parents)
    root_y = find(y, parents)

    if root_x == root_y:
        return

    if ranks[root_x] < ranks[root_y]:
        parents[root_x] = root_y
        ranks[root_y] += ranks[root_x]
    else:
        parents[root_y] = root_x
        ranks[root_x] += ranks[root_y]

def get_unconnected_points(m, segments):
    if len(segments) == 0:
        return list(range(m + 1))

    parents = [i for i in range(m + 1)]
    ranks = [1] * m  # the rank of a point will be the number of segments to which it belongs to

    result = []
    for _, [start, end] in segments:
        for point in range(start, end + 1):
            root = find(point, parents)
            ranks[root] += 1

    for point in range(m + 1):
        if find(point, parents) == point:
            result.append(point)

    return result

def segments(m, a):
