def power(a, b):
    result = 1
    while b > 0:
        if b & 1:
            result = (result * a) % MOD
        a = (a * a) % MOD
        b >>= 1
    return result

def euler_totient(n):
    totient = n
    prime_factors = []
    i = 2
    while i * i <= n:
        if n % i:
            i += 1
        else:
            count = 0
            while n % i == 0:
                count += 1
                n //= i
            totient -= totient // i ** count * (i - 1)
            prime_factors.append(i)
