        return 0
    elif n == 2:
        return 1
    elif n == 3:
        return 1
    elif n == 4:
        return 0

    odd_cnt = euler_totient(n) - 1
    return (1 + odd_cnt) * (odd_sets(n - 1) + odd_sets(n - 3) + ... + odd_sets(1))

