}

int dp_i_j(int i, int j) {
    if (i == 0 && j == 0) {
        return 0;
    }
    if (i > 0 && j > 0) {
        int min_cost = dp[i - 1][j - 1] + getHammingDistance(i, j) + getCost(swap(i, j));
        for (int k = 1; i + k <= n && j + k <= n; k++) {
