using namespace std;
using namespace boost;

typedef adjacency_list<vecS, vecS, undirectedS> Graph;
typedef graph_traits<Graph>::vertex_descriptor Vertex;

vector<int> build_vector(int n) {
    vector<int> v(2 * n);
    for (int i = 0; i < n; ++i) {
        cin >> v[i] >> v[i + n];
    }
    return v;
}

Graph build_graph(const vector<int>& v) {
    int n = v.size() / 2;
    Graph g(2 * n);

    for (int i = 0; i < 2 * n; i += 2) {
        for (int j = i + 1; j < i + 2; ++j) {
            add_edge(i, j, g);
        }
    }

    return g;
}

bool is_perfect_matching(const Graph& g, const vector<Vertex>& matching) {
    int n = num_vertices(g);
    for (int i = 0; i < n; ++i) {
        if (matching[i] == -1) {
            return false;
        } else if (incident(matching[i], g).first == incident(matching[i], g).second) {
            return false;
        }
    }
    return true;
}

int solve(const vector<int>& v) {
    int n = v.size() / 2;
    Graph g = build_graph(v);

    vector<Vertex> matching(n, -1);
    hungarian_algorithm(g, &matching[0].operator[](0), &matching[0]);

    if (!is_perfect_matching(g, matching)) {
        cerr << "No valid matching found" << endl;
        return n;
    }

    vector<bool> visited(n, false);
    vector<int> sequence(n, -1);

    function<void(Vertex)> dfs = [&](Vertex u) {
        visited[u] = true;
        sequence[u] = u;

        for (auto e : incident(u, g)) {
            Vertex v = target(e, g);
            if (!visited[v] && matching[static_cast<unsigned int>(v)] == u) {
                dfs(v);
            }
        }
    };

    for (int i = 0; i < n; ++i) {
        Vertex u = matching[i];
        if (!visited[u]) {
            dfs(u);
        }
    }

    int num_swaps = 0;
    for (int i = 0; i < n; ++i) {
        if (sequence[i] != i) {
            swap(sequence[i], sequence[i + 1]);
            ++num_swaps;
        }
    }

    return num_swaps;
}

