import collections

def is_prime(n):
    if n < 2:
        return False
    for i in range(2, int(n**0.5) + 1):
        if n % i == 0:
            return False
    return True

def get_neighbors(state):
    neighbors = []
    for i in range(3):
        for j in range(3):
            for dx, dy in [(0, 1), (1, 0)]:
                nx, ny = i + dx, j + dy
                if 0 <= nx < 3 and 0 <= ny < 3:
                    nxt = list(state)
                    nxt[3*i+j], nxt[3*nx+ny] = nxt[3*nx+ny], nxt[3*i+j]
                    if is_prime(nxt[3*i+j] + nxt[3*nx+ny]):
                        neighbors.append(''.join(nxt))
    return neighbors

def solve(state):
    goal_state = '123456789'
    queue = collections.deque([(state, 0)])
    visited = {state: 0}
    while queue:
        state, dist = queue.popleft()
        if state == goal_state:
            return dist
        for neighbor in get_neighbors(state):
            if neighbor not in visited:
                visited[neighbor] = dist + 1
                queue.append((neighbor, dist + 1))
    return -1

