private List<CSVData> CreateCSVData(string[][] csvData)
{
    List<CSVData> csvDataList = new List<CSVData>();

    for (int i = 0; i < csvData.Length; i++)
    {
        string[] dataRow = csvData[i];
        CSVData csvDatum = CreateInstance<CSVData>();
        csvDatum.data = dataRow;
        csvDataList.Add(csvDatum);
    }

    return csvDataList;
}
```
