BasicBlock* mergeBB = BasicBlock::Create(context, "ifcont");

builder.CreateCondBr(condition, thenBB, mergeBB);
builder.SetInsertPoint(thenBB);
builder.CreateBr(mergeBB);
builder.SetInsertPoint(mergeBB);

return builder.CreatePHI(Type::getInt32Ty(context), 2, "ifmerge");
}

// Emit a Rust-style while loop
void whileLoop(Value* condition, Function* loopBody) {
BasicBlock* loopHead = BasicBlock::Create(context, "loop", func);
BasicBlock* loopEnd = BasicBlock::Create(context, "end");

builder.CreateBr(loopHead);
builder.SetInsertPoint(loopHead);

Value* contVal = builder.CreateCondBr(condition, loopBody->getBasicBlockList().back(), loopEnd);

loopBody->setName("loop_body");
loopBody->moveBefore(loopEnd);

builder.CreateBr(loopHead);
builder.SetInsertPoint(loopEnd);
}

// Emit Rust-style let bindings
Value* letBinding(string name, Value* value) {
AllocaInst* alloca = builder.CreateAlloca(value->getType(), 0, name);
builder.CreateStore(value, alloca);
return alloca;
}

// Emit Rust-style match expressions
Value* matchExpr(Value* input, map<Value*, Value*> branches) {
BasicBlock* matchBB = BasicBlock::Create(context, "match", func);
BasicBlock* doneBB = BasicBlock::Create(context, "done");

builder.CreateBr(matchBB);
Value* wildcard = UndefValue::get(Type::getInt32Ty(context));

for (auto branch : branches) {
