builder.CreateCondBr(builder.CreateICmpEQ(input, branch.first), caseBB, doneBB);
builder.SetInsertPoint(caseBB);
wildcard = builder.CreatePHI(Type::getInt32Ty(context), 2, "wildcard");
wildcard = builder.CreateCast(Instruction::CastOps::Int32NE, branch.second, Type::getInt32Ty(context), "", wildcard);
}

builder.SetInsertPoint(doneBB);
return wildcard;
}

