class DistortionEffectRenderer: NSObject, SCNRendererDelegate {
    func renderer(_ renderer: SCNSceneRenderer, willRenderScene scene: SCNScene, atTime time: TimeInterval) {
        guard let lensingRingNode = scene.rootNode.childNode(withName: "lensingRingNode", recursively: false) else { return }
        guard let cameraNode = scene.rootNode.childNode(withName: "cameraNode", recursively: false) else { return }

        // Calculate the vectors from the camera to the black hole and the lensing ring
        let cameraToBlackHoleVector = cameraNode.convertPosition(blackHoleNode.position, to: nil)
        let cameraToLensingRingVector = cameraNode.convertPosition(lensingRingNode.position, to: nil)

        // Calculate the angle between the two vectors
        let dotProduct = cameraToBlackHoleVector.x * cameraToLensingRingVector.x + cameraToBlackHoleVector.y * cameraToLensingRingVector.y + cameraToBlackHoleVector.z * cameraToLensingRingVector.z
        let angle = acos(dotProduct / (cameraToBlackHoleVector.length() * cameraToLensingRingVector.length()))

        // Calculate the displacement amount based on the angle
        let displacementAmount = sin(angle * 2 * Double.pi) * 2

        // Apply the displacement to the lensing ring
        lensingRingNode.geometry?.firstMaterial?.diffuse.contentsTransform = SCNMatrix4MakeTranslation(0, displacementAmount / 10, 0)
    }
}
