<?php
namespace Sample\Customcontact\Controller\Index;

use Magento\Contact\Model\Config;
use Magento\Contact\Model\MailInterface;
use Magento\Framework\App\Action\Context;
use Magento\Framework\Controller\Result\Redirect;
use Magento\Framework\Controller\ResultFactory;
use Magento\Framework\Exception\LocalizedException;
use Magento\Framework\Translate\Inline\StateInterface;

class Index extends \Magento\Contact\Controller\Index
{
    /**
     * @var Config
     */
    protected $contactConfig;

    /**
     * @var MailInterface
     */
    protected $mail;

    /**
     * @var StateInterface
     */
    protected $inlineTranslation;

    /**
     * @param Context $context
     * @param Config $contactConfig
     * @param MailInterface $mail
     * @param StateInterface $inlineTranslation
     */
    public function __construct(
        Context $context,
        Config $contactConfig,
        MailInterface $mail,
        StateInterface $inlineTranslation
    ) {
        $this->contactConfig = $contactConfig;
        $this->mail = $mail;
        $this->inlineTranslation = $inlineTranslation;
        parent::__construct($context);
    }

    /**
     * @return Redirect
     */
    public function execute()
    {
        $post = $this->getRequest()->getPost();
        if ($post) {
            $this->inlineTranslation->suspend();
            try {
                $this->sendEmail($post);
                $this->messageManager->addSuccessMessage(
                    __('Thanks for contacting us with your comments and questions. We\'ll respond to you very soon.')
                );
                $this->_redirect('customcontact/index');
                return;
            } catch (LocalizedException $e) {
                $this->messageManager->addErrorMessage(
                    __($e->getMessage())
                );
            } catch (\Exception $e) {
                $this->messageManager->addErrorMessage(
                    __('An error occurred while processing your form. Please try again later.')
                );
            }
