```php
<?php
namespace Application\Repositories;

use Application\Models\Product;
use Application\Config\Database;

class ProductRepository
{
    private $db;

    public function __construct(Database $db)
    {
        $this->db = $db->getPdo();
    }

    public function findAll(): array
    {
        $stmt = $this->db->prepare('SELECT * FROM products');
        $stmt->execute();

        $products = [];

        while ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {
            $products[] = new Product(
                $row['id'],
                $row['name'],
                $row['price']
            );
        }

        return $products;
    }

    public function findById(int $id): ?Product
    {
        $stmt = $this->db->prepare('SELECT * FROM products WHERE id = :id');
        $stmt->bindParam(':id', $id);
        $stmt->execute();

        if ($row = $stmt->fetch(PDO::FETCH_ASSOC)) {
            return new Product(
                $row['id'],
                $row['name'],
                $row['price']
            );
        }

        return null;
    }

    public function save(Product $product): void
    {
        if ($product->getId() === null) {
            $this->insert($product);
        } else {
            $this->update($product);
        }
    }

    public function delete(int $id): void
    {
        $stmt = $this->db->prepare('DELETE FROM products WHERE id = :id');
        $stmt->bindParam(':id', $id);
        $stmt->execute();
    }

    private function insert(Product $product): void
    {
        $stmt = $this->db->prepare('INSERT INTO products (name, price) VALUES (:name, :price)');
        $stmt->bindParam(':name', $product->getName());
        $stmt->bindParam(':price', $product->getPrice());
        $stmt->execute();

        $product->setId($this->db->lastInsertId());
    }

    private function update(Product $product): void
    {
        $stmt = $this->db->prepare('UPDATE products SET name = :name, price = :price WHERE id = :id');
        $stmt->bindParam(':name', $product->getName());
        $stmt->bindParam(':price', $product->getPrice());
        $stmt->bindParam(':id', $product->getId());
        $stmt->execute();
    }
}
```

