<?php
namespace Application\Controllers;

use Psr\Http\Message\ResponseInterface as Response;
use Psr\Http\Message\ServerRequestInterface as Request;
use Application\Services\ProductService;

class ProductController
{
    private $productService;

    public function __construct(ProductService $productService)
    {
        $this->productService = $productService;
    }

    public function index(Request $request, Response $response, array $args): Response
    {
        $products = $this->productService->getAllProducts();

        $data = [
            'products' => $products
        ];

        return $this->renderHTML($request, $response, 'products.twig', $data);
    }

    public function add(Request $request, Response $response, array $args): Response
    {
        // Lógica para mostrar el formulario de adición de productos
    }

    public function edit(Request $request, Response $response, array $args): Response
    {
        $id = $args['id'];
        $product = $this->productService->getProductById($id);

        if ($product === null) {
            return $response->withStatus(404);
        }

        $data = [
            'product' => $product
        ];

        return $this->renderHTML($request, $response, 'edit_product.twig', $data);
    }

    public function delete(Request $request, Response $response, array $args): Response
    {
        $id = $args['id'];
        $this->productService->deleteProduct($id);

        return $response->withStatus(302)->withHeader('Location', '/products');
    }

    public function save(Request $request, Response $response, array $args): Response
    {
        // Lógica para procesar el formulario de adición o edición de productos
    }

    private function renderHTML(Request $request, Response $response, string $template, array $data): Response
    {
        $html = $this->getTwigEnvironment()->render($template, $data);

        $response->getBody()->write($html);

        return $response->withHeader('Content-Type', 'text/html');
    }

    private function getTwigEnvironment(): \Twig\Environment
    {
        $loader = new \Twig\Loader\FilesystemLoader('../views');
        $twig = new \Twig\Environment($loader);

        return $twig;
    }
}
