public abstract class ASTNode {
    public abstract void accept(ASTVisitor visitor);
}

public class IntLiteralNode extends ASTNode {
    private final int value;

    public IntLiteralNode(int value) {
        this.value = value;
    }

    public int getValue() {
        return value;
    }

    @Override
    public void accept(ASTVisitor visitor) {
        visitor.visit(this);
    }
}

public class IDNode extends ASTNode {
    private final String name;

    public IDNode(String name) {
        this.name = name;
    }

    public String getName() {
        return name;
    }

    @Override
    public void accept(ASTVisitor visitor) {
        visitor.visit(this);
    }
}

public class BinaryOperatorNode extends ASTNode {
    private final String operator;
    private final ASTNode leftNode;
    private final ASTNode rightNode;

    public BinaryOperatorNode(String operator, ASTNode leftNode, ASTNode rightNode) {
        this.operator = operator;
        this.leftNode = leftNode;
        this.rightNode = rightNode;
    }

    public String getOperator() {
        return operator;
    }

    public ASTNode getLeftNode() {
        return leftNode;
    }

    public ASTNode getRightNode() {
        return rightNode;
    }

    @Override
    public void accept(ASTVisitor visitor) {
        visitor.visit(this);
    }
}

public class ASTFactory {

    public static ASTNode newIntLiteralNode(int value) {
        return new IntLiteralNode(value);
    }

    public static ASTNode newIDNode(String name) {
        return new IDNode(name);
    }

    public static ASTNode newBinaryOperatorNode(String operator, ASTNode leftNode, ASTNode rightNode) {
        return new BinaryOperatorNode(operator, leftNode, rightNode);
