```java
public interface ASTVisitor {
    void visit(IntLiteralNode node);

    void visit(IDNode node);

    void visit(BinaryOperatorNode node);
}

public class Evaluator implements ASTVisitor {

    private int value;

    @Override
    public void visit(IntLiteralNode node) {
        value = node.getValue();
    }

    @Override
    public void visit(IDNode node) {
        // Look up the value of the identifier in a symbol table
        throw new UnsupportedOperationException();
    }

    @Override
    public void visit(BinaryOperatorNode node) {
        ASTNode leftNode = node.getLeftNode();
        ASTNode rightNode = node.getRightNode();
        int leftValue = 0;
        int rightValue = 0;

        if (leftNode instanceof IntLiteralNode) {
            leftValue = ((IntLiteralNode) leftNode).getValue();
        } else {
            leftNode.accept(this);
            leftValue = value;
        }

        if (rightNode instanceof IntLiteralNode) {
            rightValue = ((IntLiteralNode) rightNode).getValue();
        } else {
            rightNode.accept(this);
            rightValue = value;
        }

        switch (node.getOperator()) {
            case "+":
                value = leftValue + rightValue;
                break;
            case "-":
                value = leftValue - rightValue;
                break;
            case "*":
                value = leftValue * rightValue;
                break;
            case "/":
                value = leftValue / rightValue;
                break;
            default:
                throw new AssertionError("Unknown operator: " + node.getOperator());
        }
    }
}
```
