public class MyDataStructure {
    private int maxSize; // maximum size of the data structure
    private int[] data; // array to store data
    private int top; // top of the data structure

    public DataStructure(int size) {
      maxSize = size;
      data = new int[maxSize];
      top = 0;
    }

    // Method to add an element to the data structure
    public void push(int value) {
      if (!isFull()) {
        data[top++] = value;
      } else {
        // Error handling for full data structure
        System.out.println("Data structure is full.");
      }
    } 

    // Method to remove an element from the data structure
    public int pop() {
      if (!isEmpty()) {
        return data[--top];
      } else {
        // Error handling for empty data structure
        System.out.println("Data structure is empty.");
        return -1; 
      }
    } 

    // Method to check if the data structure is empty
    public boolean isEmpty() { 
      return top == 0;
    } 

    // Method to check if the data structure is full
    public boolean isFull() {
      return top == maxSize;
    }

    // Method to return the size of the data structure
    public int size() {
      return top;
    }
}
