        owner = msg.sender;
        eventDescription = description;
        marketCloseTime = closeTime;
    }

    // The function for users to submit their predictions
    function submitPrediction(bytes32 prediction) public payable {
        require(msg.value > 0, "You must place a bet to participate.");
        require(block.timestamp < marketCloseTime, "The market is closed.");
        predictions[prediction][bytes32(msg.sender)] += msg.value;
    }

    // The function for the oracle to report the outcome and distribute rewards
    function reportOutcome(bytes32 outcome) public {
        require(msg.sender == owner, "Only the owner can report the outcome.");
        bytes32 winningPrediction = getWinningPrediction(outcome);
        require(winningPrediction != bytes32(0), "No valid prediction was made.");
        uint totalReward = getTotalReward(winningPrediction);
        require(totalReward > 0, "No reward to distribute.");
        for (uint i = 0; i < getPredictionCount(winningPrediction); i++) {
